/**
 * Returns a promise that resolves after `duration` milliseconds. Uses setTimeout semantics.
 *
 * @function asyncWait
 *
 * @param duration {number} The number of milliseconds before the promise is resolved.
 *
 * @return {Promise}
 */
module.exports = async function asyncWait(duration) {
    return new Promise(resolve => setTimeout(resolve, duration));
};
