"use strict";

const tester = require("./framework");
const wait   = require("../source/async-wait");

module.exports = tester.run([
    tester.make("asyncWait() should wait at least `duration` ms before resolving", async () => {
        const duration = 100;
        const before   = Date.now();

        await wait(duration);

        return Date.now() - before >= duration;
    })
]);
